sys_variable_value.value:
(function execute(inputs, outputs) {
  try {
     var mandatoryFieldsList = {
      "Enqueue/Dequeue": inputs.enqdq
    };
    var emptyFields = SapEccUtil.checkMandatoryFields(mandatoryFieldsList);
    if (JSUtil.notNil(emptyFields)) {
      throw 'Mandatory field(s) '.concat(emptyFields)+' cannot be blank.';
    }

    //Retrieve connection details
    var info = SapEccUtil.getConnectionFromAlias(inputs.alias_id);
    outputs.conn_host = info.getAttribute("host");
    outputs.conn_params = info.getExtendedAttributes();

    var connId = info.getAttribute("sys_id");
    outputs.cred_id = SapEccUtil.getCredentialIdFromConnection(connId);

    outputs.rfc_name = inputs.rfc;
	var unflattendInputSchema=SapEccUtil.unflattenPayloadWithTabularFields(inputs.user_in);

    var unflattendTableSchema = SapEccUtil.unflattenPayloadWithTabularFields(inputs.table_schema);
    var inputPayload={};

    Object.keys(unflattendTableSchema).forEach(function(key) { 
        inputPayload[key] = unflattendTableSchema[key];
      });

    Object.keys(unflattendInputSchema).forEach(function(key) { 
        inputPayload[key] = unflattendInputSchema[key];
      });

    //Consolidate input fields as required by SAP
    outputs.enq_deq = inputs.enqdq;
    outputs.json_in = JSON.stringify(inputPayload);
  } catch (e) {
    throw e;
  }
})(inputs, outputs);

