sys_variable_value.value:
(function execute(inputs, outputs) {
  try
  {
      var unflattendSchema=SapEccUtil.unflattenPayloadWithTabularFields(inputs.user_input);
      if(Object.keys(unflattendSchema).length == 0){
      outputs.status = "error";
      outputs.err = "Atleast one attribute must be selected from Input Fields.";
      return;
    }
    var objectArray=[], response={};
    if(Object.keys(unflattendSchema).length > 0){
  		objectArray.push(unflattendSchema);

  	}
    outputs.status = "success";
    response[inputs.table_field]=objectArray;
    outputs.response=response;
  }catch(e){
     var errStatObj = SapEccUtil.getUnknownErr(e);
      outputs.status = errStatObj.status;
      outputs.err = errStatObj.err;
  }
})(inputs, outputs);

