sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.status = inputs.status;
  if (inputs.status == 'success') {
    try {
      var resp = JSON.parse(inputs.sapresponse);
      var isNotEmpty = sn_sap_ecc_rfc_spo.SapEccUtil.isResponseNotEmpty(resp);
      if (isNotEmpty) {
        var errStatusObj = sn_sap_ecc_rfc_spo.SapEccUtil.getVendorErrors(resp);
        outputs.response = errStatusObj.response;
        outputs.err = errStatusObj.err;
        outputs.status = errStatusObj.status;
      } else {
        outputs.status = 'error';
        outputs.err = 'No or empty response received from SAP ECC - RFC.';
      }
    } catch (e) {
      var errStatObj = sn_sap_ecc_rfc_spo.SapEccUtil.getUnknownErr(e);
      outputs.status = errStatObj.status;
      outputs.err = errStatObj.err;
    }
  }
})(inputs, outputs);

