sys_variable_value.value:
(function execute(inputs, outputs) {

  try {

    var mandatoryFields = {};
    mandatoryFields['Sort Field'] = inputs['LFA1']['SORTL'];
    mandatoryFields['Account Number of Vendor or Creditor'] = inputs['LFA1']['LIFNR'];
    mandatoryFields['Name'] = inputs['LFA1']['NAME1'];
    mandatoryFields['Country Key'] = inputs['LFA1']['LAND1'];
    mandatoryFields['Vendor Account Group'] = inputs['LFA1']['KTOKK'];
    if (!gs.nil(inputs['LFB1'])) {
      mandatoryFields['Account Number of Vendor or Creditor'] = inputs['LFB1']['LIFNR'];
      mandatoryFields['Company Code'] = inputs['LFB1']['BUKRS'];
      mandatoryFields['Reconciliation Account in General Ledger'] = inputs['LFB1']['AKONT'];
      mandatoryFields['Planning Group'] = inputs['LFB1']['FDGRV'];
    }
    if (!gs.nil(inputs['LFM1'])) {
      mandatoryFields['Account Number of Vendor or Creditor'] = inputs['LFM1']['LIFNR'];
      mandatoryFields['Purchasing Organization'] = inputs['LFM1']['EKORG'];
      mandatoryFields['Purchase Order Currency'] = inputs['LFM1']['WAERS'];
    }
    SapEccUtil.checkMandatoryFields(mandatoryFields);
    var info = SapEccUtil.getConnectionFromAlias(inputs.alias_id);
    outputs.conn_host = info.getAttribute("host");
    outputs.conn_params = info.getExtendedAttributes();
    var connId = info.getAttribute("sys_id");
    outputs.cred_id = SapEccUtil.getCredentialIdFromConnection(connId);
    var inputPayload = {};
    inputPayload['IS_VENDOR'] = {
      'LFA1': inputs['LFA1'],
      'LFB1': inputs['LFB1'],
      'LFM1': inputs['LFM1']
    };
    inputPayload['IV_SCENARIO'] = 'U';
    outputs.payload_out = JSON.stringify(inputPayload);
  } catch (e) {
    throw e;
  }

})(inputs, outputs);

