sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var inputs = SapEccUtil.trimInputs(inputs);

        inputs.InvoiceItemData = SapEccUtil.filterArrayObjects(inputs.InvoiceItemData);

        //Validate mandatory fields
        var mandatoryFieldsList = {
            "Invoice Header > Invoice": inputs.header.INVOICE_IND,
            "Invoice Header > Document Type": inputs.header.DOC_TYPE,
            "Invoice Header > Document Date": inputs.header.DOC_DATE,
            "Invoice Header > Posting Date": inputs.header.PSTNG_DATE,
            "Invoice Header > Company Code": inputs.header.COMP_CODE,
            "Invoice Header > Terms of Payment": inputs.header.PMNTTRMS,
            "Invoice Header > Currency": inputs.header.CURRENCY,
            "Invoice Header > Gross Invoice Amount": inputs.header.GROSS_AMOUNT
        };

        if (inputs.InvoiceItemData.length != 0) {
            var itemdataArr = inputs.InvoiceItemData;
            for (var index = 0; index < itemdataArr.length; ++index) {
                var itemdataNum = index + 1;
                var itemdataElement = 'InvoiceItemData [' + itemdataNum + ']';
                mandatoryFieldsList[itemdataElement + " > Invoice Item"] = itemdataArr[index].INVOICE_DOC_ITEM;
                mandatoryFieldsList[itemdataElement + " > Purchase order"] = itemdataArr[index].PO_NUMBER;
                mandatoryFieldsList[itemdataElement + " > Item"] = itemdataArr[index].PO_ITEM;
                mandatoryFieldsList[itemdataElement + " > Amount"] = itemdataArr[index].ITEM_AMOUNT;
                mandatoryFieldsList[itemdataElement + " > Quantity"] = itemdataArr[index].QUANTITY;
                mandatoryFieldsList[itemdataElement + " > Order Unit"] = itemdataArr[index].PO_UNIT;
            }
        }
        var emptyFields = SapEccUtil.checkMandatoryFields(mandatoryFieldsList);
        if (JSUtil.notNil(emptyFields)) {
            throw 'Mandatory field(s) '.concat(emptyFields) + ' cannot be blank.';
        }

        var info = SapEccUtil.getConnectionFromAlias(inputs.alias_id);
        outputs.conn_host = info.getAttribute("host");
        outputs.conn_params = info.getExtendedAttributes();

        var connId = info.getAttribute("sys_id");
        outputs.cred_id = SapEccUtil.getCredentialIdFromConnection(connId);

        var payload = SapEccUtil.unflattenPayload(inputs.additional_in);
        if (inputs.bapi == "BAPI_INCOMINGINVOICE_CREATE") {
            payload['HEADERDATA'] = inputs.header;
            if (inputs.InvoiceItemData.length != 0) {
                payload['ITEMDATA'] = inputs.InvoiceItemData;
            }
        } else if (inputs.bapi == "ZSN_BAPI_INCOMINGINV_CREATE") {
            payload['IV_HEADERDATA'] = inputs.header;
            if (inputs.InvoiceItemData.length != 0) {
                payload['IT_ITEMDATA'] = inputs.InvoiceItemData;
            }
        } else
            throw "The selected BAPI is not valid."

        outputs.payload = SapEccUtil.removeMetaNameField(payload);
    } catch (e) {
        throw e;
    }
})(inputs, outputs);

