sys_variable_value.value:
(function execute(inputs, outputs) {
  try {

    var inputs = SapRFCUtil.EscapeHTMLInjection(inputs);

    //Validate mandatory fields
    var mandatoryFieldsList = {
      "Order Header > Sales Document Type": inputs.header.DOC_TYPE,  
      "Order Header > Sales Organization": inputs.header.SALES_ORG,
      "Order Header > Distribution Channel": inputs.header.DISTR_CHAN
    };

    var emptyFields = SapRFCUtil.checkMandatoryFields(mandatoryFieldsList);
    if (JSUtil.notNil(emptyFields)) {
      throw 'Mandatory field(s) '.concat(emptyFields)+' cannot be blank.';
    }

    //Retrieve connection details
    var info = SapRFCUtil.getConnectionFromAlias(inputs.alias_id);
    outputs.conn_host = info.getAttribute("host");
    outputs.conn_params = info.getExtendedAttributes();

    var connId = info.getAttribute("sys_id");
    outputs.cred_id = SapRFCUtil.getCredentialIdFromConnection(connId);

    //Creating X fields
    if (inputs.header != null) {
      inputs['ORDER_HEADER_INX'] = new Object();
      for (genKey in inputs.header) {
        var isnotrequired = SapRFCUtil.checkextrafields(genKey,'BAPI_SALESORDER_CREATEFROMDAT2-ORDER_HEADER_INX');  
        if (inputs.header[genKey] != null && inputs.header[genKey] !== '' && !isnotrequired) {
          inputs.ORDER_HEADER_INX[genKey] = 'X';
        }
      }
    }
    inputs.ORDER_HEADER_INX['UPDATEFLAG']='X';
    inputs.ORDER_HEADER_INX['PROMOTION']='X';
    inputs.ORDER_HEADER_INX['POITM_NO_S']='X';



    if (inputs.item != null && !JSUtil.isEmpty(inputs.item)) {
      inputs['ORDER_ITEMS_INX'] = new Array();
      for (var i = 0; i < inputs.item.length; i++) {
        var itemX = new Object();
        var item = inputs.item[i];
        for (itemkey in item) {
          var hasVal = item[itemkey] != null && item[itemkey] !== '';
          var isnotrequire = SapRFCUtil.checkextrafields(itemkey,'BAPI_SALESORDER_CREATEFROMDAT2-ORDER_ITEMS_INX');
          if (hasVal && !isnotrequire && itemkey !== 'ITM_NUMBER') {
            itemX[itemkey] = 'X';
          } 
          else if (hasVal && !isnotrequire) {
            itemX[itemkey] = item[itemkey];
          }
          itemX['UPDATEFLAG'] = 'X';
        }
        if (Object.keys(itemX).length !== 0) {
          inputs.ORDER_ITEMS_INX.push(itemX);
        }
      }
    }


    if (inputs.schedule != null && !JSUtil.isEmpty(inputs.schedule)) {
      inputs['ORDER_SCHEDULES_INX'] = new Array();
      for (var j = 0; j < inputs.schedule.length; j++) {
        var scheduleX = new Object();
        var schedule = inputs.schedule[j];
        for (schedulekey in schedule) {
          var hasVal1 = schedule[schedulekey] != null && schedule[schedulekey] !== '';
          var cond = schedulekey !== 'ITM_NUMBER' && schedulekey !=='SCHED_LINE';
          if (hasVal1 && cond) {
            scheduleX[schedulekey] = "X";
          }else if (hasVal1) {
            scheduleX[schedulekey] = schedule[schedulekey];
          }
        }
        if (Object.keys(scheduleX).length !== 0) {
          inputs.ORDER_SCHEDULES_INX.push(scheduleX);
        }
      }
    }


    //Consolidate input fields as required by SAP
    var payload = SapRFCUtil.unflattenPayload(inputs.additional_in);
    payload['ORDER_HEADER_IN'] = inputs.header;
    payload['ORDER_HEADER_INX'] = inputs.ORDER_HEADER_INX;
    payload['ORDER_ITEMS_IN'] = inputs.item;
    payload['ORDER_ITEMS_INX'] = inputs.ORDER_ITEMS_INX;
    payload['ORDER_PARTNERS'] = inputs.comm;
    payload['ORDER_SCHEDULES_IN'] = inputs.schedule;
    payload['ORDER_SCHEDULES_INX'] = inputs.ORDER_SCHEDULES_INX;

    outputs.json_in = SapRFCUtil.removeMetaNameField(payload);
  } catch (e) {
    throw e;
  }
})(inputs, outputs);

