sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var inputs = SapEccUtil.trimInputs(inputs);

        inputs.PurchaseOrderItems = SapEccUtil.filterArrayObjects(inputs.PurchaseOrderItems);
        inputs.PoDelivery = SapEccUtil.filterArrayObjects(inputs.PoDelivery);
        inputs.PoAccount = SapEccUtil.filterArrayObjects(inputs.PoAccount);
        inputs.PoSchedule = SapEccUtil.filterArrayObjects(inputs.PoSchedule);
        inputs.PoLimits = SapEccUtil.filterArrayObjects(inputs.PoLimits);
        inputs.PoServiceLines = SapEccUtil.filterArrayObjects(inputs.PoServiceLines);
      	inputs.serviceLineAccount = SapEccUtil.filterArrayObjects(inputs.serviceLineAccount);

        //Validate mandatory fields
        var mandatoryFieldsList = {
            "PO Header > Company Code": inputs.header.COMP_CODE,
            "PO Header > Purchasing Doc Type": inputs.header.DOC_TYPE,
            "PO Header > Item Number Interval": inputs.header.ITEM_INTVL,
            "PO Header > Vendor [ VENDOR ]": inputs.header.VENDOR,
            "PO Header > Purch Organization": inputs.header.PURCH_ORG,
            "PO Header > Purchasing Group": inputs.header.PUR_GROUP
        };

        if (inputs.PurchaseOrderItems.length != 0) {
            var itemdataArr = inputs.PurchaseOrderItems;
            for (var index = 0; index < itemdataArr.length; ++index) {
                var itemdataNum = index + 1;
                var itemdataElement = 'Purchase Order Items [' + itemdataNum + ']';
                mandatoryFieldsList[itemdataElement + " > Po Item"] = itemdataArr[index].PO_ITEM;
            }
        }

        var emptyFields = SapEccUtil.checkMandatoryFields(mandatoryFieldsList);
        if (JSUtil.notNil(emptyFields)) {
            throw 'Mandatory field(s) '.concat(emptyFields) + ' cannot be blank.';
        }

        var info = SapEccUtil.getConnectionFromAlias(inputs.alias_id);
        outputs.conn_host = info.getAttribute("host");
        outputs.conn_params = info.getExtendedAttributes();

        var connId = info.getAttribute("sys_id");
        outputs.cred_id = SapEccUtil.getCredentialIdFromConnection(connId);

        if (inputs.header != null) {
            inputs['POHEADERX'] = new Object();
            for (genKey in inputs.header) {
                if (inputs.header[genKey] != null && inputs.header[genKey] !== '') {
                    inputs.POHEADERX[genKey] = 'X';
                }
            }
        }

        if (inputs.PurchaseOrderItems != null && !JSUtil.isEmpty(inputs.PurchaseOrderItems)) {
            inputs['POITEMX'] = new Array();
            for (var i = 0; i < inputs.PurchaseOrderItems.length; i++) {
                var poitemX = new Object();
                var poitem = inputs.PurchaseOrderItems[i];
                for (itemKey in poitem) {
                    var hasVal = poitem[itemKey] != null && poitem[itemKey] != '';
                    if (hasVal && itemKey !== 'PO_ITEM') {
                        poitemX[itemKey] = 'X';
                    } else if (hasVal) {
                        poitemX[itemKey] = poitem.PO_ITEM;
                    }
                }
                if (Object.keys(poitemX).length !== 0) {
                    inputs.POITEMX.push(poitemX);
                }
            }
        }

        if (inputs.PoSchedule != null && !JSUtil.isEmpty(inputs.PoSchedule)) {
            inputs['POSCHEDULEX'] = new Array();
            for (var i = 0; i < inputs.PoSchedule.length; i++) {
                var poscheduleX = new Object();
                var poschedule = inputs.PoSchedule[i];
                for (itemKey in poschedule) {
                    var hasVal = poschedule[itemKey] != null && poschedule[itemKey] != '';
                    if (hasVal && itemKey !== 'PO_ITEM' && itemKey !== 'SCHED_LINE') {
                        poscheduleX[itemKey] = 'X';
                    } else if (hasVal) {
                        poscheduleX[itemKey] = poschedule[itemKey];
                    }
                }
                if (Object.keys(poscheduleX).length !== 0) {
                    inputs.POSCHEDULEX.push(poscheduleX);
                }
            }
        }


        if (inputs.PoAccount != null && !JSUtil.isEmpty(inputs.PoAccount)) {
            inputs['POACCOUNTX'] = new Array();
            for (var i = 0; i < inputs.PoAccount.length; i++) {
                var poaccountX = new Object();
                var poaccount = inputs.PoAccount[i];
                for (itemKey in poaccount) {
                    var hasVal = poaccount[itemKey] != null && poaccount[itemKey] != '';
                    if (hasVal && itemKey !== 'PO_ITEM' && itemKey !== 'SERIAL_NO') {
                        poaccountX[itemKey] = 'X';
                    } else if (hasVal) {
                        poaccountX[itemKey] = poaccount[itemKey];
                    }
                }
                if (Object.keys(poaccountX).length !== 0) {
                    inputs.POACCOUNTX.push(poaccountX);
                }
            }
        }

        var payload = SapEccUtil.unflattenPayload(inputs.additional_in);
        if (inputs.bapi == "BAPI_PO_CREATE1") {
            payload['POHEADER'] = inputs.header;
            payload['POHEADERX'] = inputs.POHEADERX;
            payload['NO_PRICE_FROM_PO'] = inputs.PriceAdoption;
            if (inputs.PurchaseOrderItems.length != 0) {
                payload['POITEM'] = inputs.PurchaseOrderItems;
                payload['POITEMX'] = inputs.POITEMX;
            }
            if (inputs.PoAccount.length != 0) {
                payload['POACCOUNT'] = inputs.PoAccount;
                payload['POACCOUNTX'] = inputs.POACCOUNTX;
            }
            if (inputs.PoDelivery.length != 0) {
                payload['POADDRDELIVERY'] = inputs.PoDelivery;
            }
            if (inputs.PoLimits.length != 0) {
                payload['POLIMITS'] = inputs.PoLimits;
            }
            if (inputs.PoServiceLines.length != 0) {
                payload['POSERVICES'] = inputs.PoServiceLines;
            }
            if (inputs.PoSchedule.length != 0) {
                payload['POSCHEDULE'] = inputs.PoSchedule;
                payload['POSCHEDULEX'] = inputs.POSCHEDULEX;
            }
          	if (inputs.serviceLineAccount.length != 0) {
                payload['POSRVACCESSVALUES'] = inputs.serviceLineAccount;
            }
        } else if (inputs.bapi == "ZSN_BAPI_PO_CREATE") {
            payload['IV_POHEADER'] = inputs.header;
            payload['IV_POHEADERX'] = inputs.POHEADERX;
            payload['IV_NO_PRICE_FROM_PO'] = inputs.PriceAdoption;
            if (inputs.PurchaseOrderItems.length != 0) {
                payload['IT_POITEM'] = inputs.PurchaseOrderItems;
                payload['IT_POITEMX'] = inputs.POITEMX;
            }
            if (inputs.PoAccount.length != 0) {
                payload['IT_POACCOUNT'] = inputs.PoAccount;
                payload['IT_POACCOUNTX'] = inputs.POACCOUNTX;
            }
            if (inputs.PoDelivery.length != 0) {
                payload['IT_POADDRDELIVERY'] = inputs.PoDelivery;
            }
            if (inputs.PoLimits.length != 0) {
                payload['IT_POLIMITS'] = inputs.PoLimits;
            }
            if (inputs.PoServiceLines.length != 0) {
                payload['IT_POSERVICES'] = inputs.PoServiceLines;
            }
            if (inputs.PoSchedule.length != 0) {
                payload['IT_POSCHEDULE'] = inputs.PoSchedule;
                payload['IT_POSCHEDULEX'] = inputs.POSCHEDULEX;
            }
          	if (inputs.serviceLineAccount.length != 0) {
                payload['IT_POSRVACCESSVALUES'] = inputs.serviceLineAccount;
            }
        } else
            throw "The selected BAPI is not valid."

        outputs.payload = SapEccUtil.removeMetaNameField(payload);
    } catch (e) {
        throw e;
    }
})(inputs, outputs);

