sys_variable_value.value:
(function execute(inputs, outputs) {

    try {
        var plants = inputs.plants;
        var matnr = inputs.matnr;
        var info = SapEccUtil.getConnectionFromAlias(inputs.alias_id);
        var connId = info.getAttribute("sys_id");
        outputs.cred_id = SapEccUtil.getCredentialIdFromConnection(connId);
        outputs.conn_host = info.getAttribute("host");
        outputs.conn_params = info.getExtendedAttributes();

        //Consolidate input fields as required by SAP
        if (inputs.selectedBapi == 'BAPI_MATERIAL_GETLIST') {
            var payload = SapEccUtil.unflattenPayload(inputs.additional_in);
            payload['PLANTSELECTION'] = plants;
            payload['MATNRSELECTION'] = matnr;
        } else if (inputs.selectedBapi == 'ZSN_BAPI_GETMATERIALDATA') {
            var payload = SapEccUtil.unflattenPayload(inputs.additional_in);
            if (plants != null && !JSUtil.isEmpty(plants)) {
                inputs['IT_PLANT'] = new Array();
                if (inputs.matnr != null && !JSUtil.isEmpty(inputs.matnr) && (inputs.matnr[0].SIGN != '' || inputs.matnr[0].OPTION != '')) {
                    throw "Material details should not be entered when Custom BAPI is selected. ";
                }
                for (var i = 0; i < inputs.plants.length; i++) {
                    var plantX = new Object();
                    var plant = inputs.plants[i];
                    for (key in plant) {
                        if (key == 'PLANT_LOW')
                            plantX['LOW'] = plant[key];
                        else if (key == 'PLANT_HIGH')
                            plantX['HIGH'] = plant[key];
                        else
                            plantX[key] = plant[key];
                    }
                    if (Object.keys(plantX).length !== 0 && (!gs.nil(plantX['LOW']) || !gs.nil(plantX['HIGH']))) {
                        inputs.IT_PLANT.push(plantX);
                        payload['IT_PLANT'] = inputs.IT_PLANT;
                    }
                }
            }
            if (payload['IV_LOAD_COMPLETE'] == 'true' || payload['IV_LOAD_COMPLETE'] == 'True')
                payload['IV_LOAD_COMPLETE'] = 'X';
            else
                payload['IV_LOAD_COMPLETE'] = '';

            if (payload['IV_INITIAL_LOAD'] == 'true' || payload['IV_INITIAL_LOAD'] == 'True')
                payload['IV_INITIAL_LOAD'] = 'X';
            else
                payload['IV_INITIAL_LOAD'] = '';

        } else 
            throw "The selected BAPI is not valid";

        outputs.json_in = SapEccUtil.removeMetaNameField(payload);
    } catch (e) {
        throw e;
    }

})(inputs, outputs);

