sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var inputs = SapEccUtil.trimInputs(inputs);
        //Validate mandatory fields
        var mandatoryFieldsList = {
            "Invoice Document Number": inputs.invoiceDocNumber,
            "Fiscal Year": inputs.fiscalYear,
            "Reversal Reason": inputs.reversalReason,
            "Reversal Document Posting Date": inputs.postingDate
        };

        var emptyFields = SapEccUtil.checkMandatoryFields(mandatoryFieldsList);
        if (JSUtil.notNil(emptyFields)) {
            throw 'Mandatory field(s) '.concat(emptyFields) + ' cannot be blank.';
        }

        var info = SapEccUtil.getConnectionFromAlias(inputs.alias_id);
        outputs.conn_host = info.getAttribute("host");
        outputs.conn_params = info.getExtendedAttributes();

        var connId = info.getAttribute("sys_id");
        outputs.cred_id = SapEccUtil.getCredentialIdFromConnection(connId);
        var payload = SapEccUtil.unflattenPayload(inputs.additional_in);
        if (inputs.bapi == "BAPI_INCOMINGINVOICE_CANCEL") {
            payload['INVOICEDOCNUMBER'] = inputs.invoiceDocNumber;
            payload['FISCALYEAR'] = inputs.fiscalYear;
            payload['REASONREVERSAL'] = inputs.reversalReason;
            payload['POSTINGDATE'] = inputs.postingDate;
        } else if (inputs.bapi == "ZSN_BAPI_INCOMINGINV_CANCEL") {
            payload['IV_INVOICEDOCNUMBER'] = inputs.invoiceDocNumber;
            payload['IV_FISCALYEAR'] = inputs.fiscalYear;
            payload['IV_REASONREVERSAL'] = inputs.reversalReason;
            payload['IV_POSTINGDATE'] = inputs.postingDate;
        } else {
            throw "The selected BAPI is not valid."
        }
        outputs.payload = SapEccUtil.removeMetaNameField(payload);
    } catch (e) {
        throw e;
    }
})(inputs, outputs);

