sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.status = inputs.status;
  if (inputs.status == 'success') {
    try {
      //Parse stringified JSON to JS Object
      outputs.response = JSON.parse(inputs.sapresponse);
      var isNotEmpty = SapEccUtil.isResponseNotEmpty(outputs.response);
      if (isNotEmpty) {
        //Retrieve SAP execution errors if any and update status
        var errStatusObj = SapEccUtil.getReturnErrors(outputs.response);
        outputs.status = errStatusObj.status;
        outputs.err = errStatusObj.err;
      } else {
        outputs.status = 'error';
        outputs.err = 'No or empty response received from SAP ECC - RFC.';
      }
    } catch (e) {
      var errStatObj = SapEccUtil.getUnknownErr(e);
      outputs.status = errStatObj.status;
      outputs.err = errStatObj.err;
    }
  }
})(inputs, outputs);

(function execute(inputs, outputs) {
  try {
    //Validate mandatory fields
    var mandatoryFieldsList = new Object();

    var plant = inputs.plant;
 /*   if (JSUtil.notNil(plant)) {
      for (var idx = 0; idx < plant.length; ++idx) {
        var plantElement = 'Plant Range Criteria [' + (idx + 1) + ']';
        mandatoryFieldsList[plantElement + " > Inclusive/exclusive"] = plant[idx].SIGN;
        mandatoryFieldsList[plantElement + " > Selection operator OPTION"] = plant[idx].OPTION;
        mandatoryFieldsList[plantElement + " > From plant"] = plant[idx].PLANT_LOW;
      }
    }*/

    var matnr = inputs.matnr;
  /*  if (JSUtil.notNil(matnr)) {
      for (var idx = 0; idx < matnr.length; ++idx) {
        var matnrElement = 'Material Number Range Criteria [' + (idx + 1) + ']';
        mandatoryFieldsList[matnrElement + " > Inclusive/exclusive"] = matnr[idx].SIGN;
        mandatoryFieldsList[matnrElement + " > Selection operator OPTION"] = matnr[idx].OPTION;
        mandatoryFieldsList[matnrElement + " > From material number"] = matnr[idx].MATNR_LOW;
      }
    }

    var emptyFields = SapEccUtil.checkMandatoryFields(mandatoryFieldsList);
    if (JSUtil.notNil(emptyFields)) {
      throw 'Mandatory field(s) cannot be blank - '.concat(emptyFields);
    }*/

    //Retrieve connection details
    var info = SapEccUtil.getConnectionFromAlias(inputs.alias_id);
    outputs.conn_host = info.getAttribute("host");
    outputs.conn_params = info.getExtendedAttributes();

    var connId = info.getAttribute("sys_id");
    outputs.cred_id = SapEccUtil.getCredentialIdFromConnection(connId);

    //Consolidate input fields as required by SAP
    var payload = SapEccUtil.unflattenPayload(inputs.additional_in);
    payload['PLANTSELECTION'] = plant;
    payload['MATNRSELECTION'] = matnr;

    outputs.json_in = SapEccUtil.removeMetaNameField(payload);
  } catch (e) {
    throw e;
  }
})(inputs, outputs);

(function execute(inputs, outputs) {
    try {
        var credProvFactory = Packages.com.snc.commons.credentials.CredentialsProviderFactory;
        var credProv = credProvFactory.getCredentialsProvider();
        var cred = credProv.getCredentialByID(inputs.cred_id);
        if (JSUtil.nil(cred)) {
            throw new Error('The Credential associated with Connection is not active.');
        }

        var connMap = new Packages.java.util.HashMap();
        connMap.put("jco.client.ashost", inputs.conn_host);
        connMap.put("jco.client.sysnr", inputs.conn_params.get("u_system_number"));
        connMap.put("jco.client.lang", inputs.conn_params.get("u_lang"));
        connMap.put("jco.client.client", inputs.conn_params.get("u_client"));
        connMap.put("jco.client.mshost", inputs.conn_params.get("u_host"));
        connMap.put("jco.client.group", inputs.conn_params.get("u_group"));
        connMap.put("jco.client.msserv", inputs.conn_params.get("u_message_server"));
        connMap.put("jco.client.user", cred.getAttribute("user_name"));
        connMap.put("jco.client.passwd", cred.getAttribute("password"));

        var opProp = new Packages.java.util.HashMap();
        opProp.put("RFC", "BAPI_MATERIAL_GETLIST");

        var conn = new Packages.com.snow.connector.sap.connection.SapConnector(connMap);
        outputs.response = conn.executeRfc(inputs.payload, opProp);
        outputs.status = 'success';
    } catch (e) {
        outputs.status = 'error';
        throw e;
    }
})(inputs, outputs);

