sys_variable_value.value:
(function execute(inputs, outputs) {
  try {
    var credProvFactory = Packages.com.snc.commons.credentials.CredentialsProviderFactory;
    var credProv = credProvFactory.getCredentialsProvider();
    var cred = credProv.getCredentialByID(inputs.cred_id);
    if (JSUtil.nil(cred)) {
      throw new Error('The Credential associated with Connection is not active.');
    }

    var connMap = new Packages.java.util.HashMap();
    connMap.put("jco.client.ashost", inputs.conn_host);
    connMap.put("jco.client.sysnr", inputs.conn_params.get("u_system_number"));
    connMap.put("jco.client.lang", inputs.conn_params.get("u_lang"));
    connMap.put("jco.client.client", inputs.conn_params.get("u_client"));
    connMap.put("jco.client.user", cred.getAttribute("user_name"));
    connMap.put("jco.client.passwd", cred.getAttribute("password"));

    var conn = new Packages.com.snow.connector.sap.connection.SapConnector(connMap);
    outputs.response = conn.getRfcInputSchema(inputs.rfc);
    outputs.status = 'success';
  } catch (e) {
    outputs.status = 'error';
    throw e;
  }
})(inputs, outputs);

