sys_variable_value.value:
(function execute(inputs, outputs) {
	outputs.status = inputs.status;
	if (inputs.status == 'success') {
		try {
			//Parse stringified JSON to JS Object
			var schema = JSON.parse(inputs.sapresponse);
			var dataArray = schema["data"];

			for (var i = 0; i < dataArray.length; i++) {
				var tableFieldObject = dataArray[i];
				if (tableFieldObject["name"] == inputs.fieldName && tableFieldObject["type"] != "array.object") {
					outputs.status = "error";
					outputs.err = "Field type is not array.object.";
					break;
				} else if (tableFieldObject["name"] == inputs.fieldName) {
					var finalObj = {};
					finalObj.data = tableFieldObject["children"];
					outputs.flat_schema = SapRFCUtil.flattenSchema(finalObj);
					break;

				}
			}
			if (i == dataArray.length) {
				outputs.status = "error";
				outputs.err = "Schema for given field not found.";
			}
		} catch (e) {
			var errStatObj = SapRFCUtil.getUnknownErr(e);
			outputs.status = errStatObj.status;
			outputs.err = errStatObj.err;
		}
	}
})(inputs, outputs);

