sys_variable_value.value:
(function execute(inputs, outputs) {

	try {
		var inputs = SapEccUtil.trimInputs(inputs);

		//Retrieve connection details
		var info = SapEccUtil.getConnectionFromAlias(inputs.alias_id);
		outputs.conn_host = info.getAttribute("host");
		outputs.conn_params = info.getExtendedAttributes();
		var connId = info.getAttribute("sys_id");
		outputs.cred_id = SapEccUtil.getCredentialIdFromConnection(connId);
		var inputPayload = {};

      	//filter company code
        var comp = SapEccUtil.filterArrayObjects(inputs.company_code);

      	//payload
      	var inp_load = SapEccUtil.getInitialLoad(inputs.initial_load);
      	var load_complete = SapEccUtil.getLoadComplete(inputs.load_complete, inputs.row_skip, inputs.row_count);
		inputPayload['IV_INITIAL_LOAD'] = inp_load;
		inputPayload['IV_ROWSKIPS'] = inputs.row_skip;
		inputPayload['IV_ROWCOUNT'] = inputs.row_count;
      	inputPayload['IV_LOAD_COMPLETE'] = load_complete;
        if(comp.length != 0){
              inputPayload['IT_COMP_CODE'] = comp;
        }
		outputs.payload = JSON.stringify(inputPayload);
	} catch (e) {
		throw e;
	}

})(inputs, outputs);

