sys_variable_value.value:
(function execute(inputs, outputs) {

	outputs.status = inputs.status;
	if (inputs.status == 'success') {
		try {
			//Parse stringified JSON to JS Object
			var resp = JSON.parse(inputs.sapresponse);
			outputs.ET_VENDOR = resp.ET_VENDOR;
			outputs.ET_TOTAL_ROWS = resp.ET_TOTAL_ROWS;
			var isNotEmpty = SapEccUtil.isResponseNotEmpty(resp);
			if (isNotEmpty) {
				//Retrieve SAP execution errors if any and update status
				var errStatusObj = SapEccUtil.getReturnErrors(resp);
				outputs.status = errStatusObj.status;
				outputs.error = errStatusObj.err;
			} else {
				outputs.status = 'error';
				outputs.error = 'No or empty response received from SAP ECC - RFC.';
			}
		} catch (e) {
			var errStatObj = SapEccUtil.getUnknownErr(e);
			outputs.status = errStatObj.status;
			outputs.error = errStatObj.err;
		}
	}

})(inputs, outputs);

